<?php      //POST	    $token = 'gf75hjkf0JKhfJ7f8fdkJH';  		$requestArray = array();      $requestArray['id'] = 199;    $requestArray['limit'] = 10;            ksort($requestArray);    $q = '';        foreach ($requestArray as $k => $v) {         $q .= $k . '=' . $v;    }        $requestArray['sig'] = md5($q . $token);	        $ch = curl_init('../api/v1/products');    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);    curl_setopt($ch, CURLOPT_POST, 1);    curl_setopt($ch, CURLOPT_POSTFIELDS, $requestArray);    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10);    curl_exec($ch);    curl_close($ch);		//DELETE	curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "DELETE");		//PUT	curl_setopt($ch, CURLOPT_POST, true);	curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "PUT");	curl_setopt($ch, CURLOPT_POSTFIELDS, $requestArray);